(function($){var methods={destroy:function(options){return $(this).each(function(){$(this).removeData('BirdOnIt')})}};$.fn.BirdOnIt=function(options){var defaults={file:"birds-svg.xml",scale:.3,onBefore:function(){},onComplete:function(){}};var settings=$.extend({},defaults,options),x,y,flip,$this=this;$.ajax({type:"GET",url:settings.file,beforeSend:settings.onBefore,dataType:"xml",success:function(xml){init(xml,settings,$this)},complete:settings.onComplete})}}(jQuery));function init(xml,settings,$this){var svg=$(xml).find('svg'),svg_number=svg.length;$this.each(function(){var corner=Math.floor((Math.random()*4)+1),random_svg=Math.floor((Math.random()*svg_number));switch(corner){case 1:x="left: ",y="top: 0;";flip="flip";break;case 2:x="right: ",y="top: ";flip="";break;case 3:x="left: ",y="bottom: ";flip="flip";break;default:x="right: ",y="bottom: ";flip="";break}var size=($this.width()*settings.scale);$(this).addClass('birdOnItContainer');$(this).prepend('<img class="'+flip+' birdOnIt" style="'+x+'-'+(size/2)+'px; '+y+'0px; width: '+size+'px;" src="'+svg.eq(random_svg-1).text()+'" />')})}